components/header/header-branding:
{{#if settings.store_logo.image.data}}
  <a class="header-branding-logo" href="/">
    <img src="{{getImage settings.store_logo.image 'logo'}}" alt="{{settings.store_logo.image.alt}}">
  </a>
{{else}}
  <h1 class="header-branding-text">
    <a href="/">{{settings.store_logo.title}}</a>
  </h1>
{{/if}}

components/header/header-navigation-recu:
{{#if children}}
  <ul class="header-navigation-list extended-tier" data-extended-tier>

    {{#each children}}
      <li class="header-navigation-list-item {{#if children}}has-children{{/if}}"
        {{#if children}}data-dropdown-toggle{{/if}}
        {{#if ../../products}}data-products-toggle{{/if}}>
        <a href="{{url}}" data-url="{{url}}">
          <span class="header-navigation-list-item-label">
            {{name}}

            {{#if children}}
              <span class="header-navigation-list-item-arrow">
                <svg><use xlink:href="#icon-chevron-right"></svg>
              </span>

              <span class="header-navigation-list-item-toggle">
                <span class="header-navigation-list-item-toggle-bar"></span>
                <span class="header-navigation-list-item-toggle-bar"></span>
              </span>
            {{/if}}
          </span>
        </a>
        {{> components/header/header-navigation-recursive}}
      </li>
    {{/each}}

    <li class="header-navigation-list-item view-all-link">
      <a href="{{url}}"><span class="header-navigation-list-item-label">{{lang 'header.navigation.view_all' category=name}}</span></a>
    </li>

  </ul>
{{/if}}

components/header/header-mobile-actions:
{{#if settings.account_creation_enabled}}
  <ul class="header-mobile-actions-list">
    {{#if customer}}
      <li class="header-mobile-actions-list-item">
        <a href="{{urls.account.index}}">
          {{lang 'footer.your_account'}}
        </a>
      </li>

      <li class="header-mobile-actions-list-item">
        <a href="{{urls.auth.logout}}">
          {{lang 'footer.log_out'}}
        </a>
      </li>
    {{else}}
      <li class="header-mobile-actions-list-item">
        <a href="{{urls.auth.create_account}}">
          {{lang 'footer.sign_up'}}
        </a>
      </li>

      <li class="header-mobile-actions-list-item">
        <a href="{{urls.auth.login}}">
          {{lang 'footer.log_in'}}
        </a>
      </li>
    {{/if}}
  </ul>
{{/if}}

components/cart/cart-preview/cart-previe:
{{#if cart.items}}
  <div class="cart-preview-item-list" data-cart-preview-item-list>
    {{#each cart.items}}
      <div class="cart-preview-item">
        <div class="cart-preview-item-details">
          <h4 class="cart-preview-item-title">
            {{#if url}}
              <a href="{{url}}">{{name}}</a>
            {{else}}
              <a href="{{edit_url}}">{{name}}</a>
            {{/if}}
          </h4>

          <p
            class="
              cart-preview-item-price
              {{#if price_discounted}}
                price-discounted
              {{/if}}
            "
          >
            {{price.formatted}}
            <span>&times; {{quantity}}</span>
          </p>

          {{#if price_discounted}}
            <p class="cart-preview-item-price">
              {{price_discounted.formatted}}
              <span>&times; {{quantity}}</span>
            </p>
          {{/if}}

          <a
            class="cart-item-remove"
            href="{{remove_url}}"
            data-cart-preview-item-remove
            data-product-id="{{id}}"
          >
            {{lang 'cart.remove'}}
          </a>
        </div>

        <figure class="cart-preview-item-image">
          {{#if type '===' 'GiftCertificate'}}
            <a
              href="javascript:void(0)"
              style="background-image:url({{cdn '/assets/img/giftcard-image.svg'}});"
            >
              <img
                src="{{cdn '/assets/img/giftcard-image.svg'}}"
                alt="{{recipient}}"
              >
            </a>
          {{else}}
            {{#if image}}
              <a href="{{url}}">
                {{> components/common/responsive-image
                  image=image
                  fit="cover"
                  fallback_size=../../../theme_settings.small
                  lazyload=../../../theme_settings.lazyload_mode
                  default_image=../../../theme_settings.default_image
                }}
              </a>
            {{else}}
              <a
                href="{{edit_url}}"
                style="background-image:url({{cdn '/assets/img/no-image.svg'}});"
              >
                <img
                  class="no-image"
                  src="{{cdn '/assets/img/no-image.svg'}}"
                  alt="{{name}}"
                >
              </a>
            {{/if}}
          {{/if}}
        </figure>
      </div>
    {{/each}}
  </div>

  <footer class="cart-preview-footer" data-cart-preview-footer>
    <h5 class="cart-preview-footer-title">
      {{lang 'cart.subtotal'}}
      <span class="cart-preview-footer-subtotal">
        {{cart.sub_total.formatted}}
      </span>
    </h5>

    <div class="cart-preview-button-wrapper">
      <a class="button" href="{{urls.checkout.single_address}}">
        {{lang 'cart.cart_preview.check_out'}}
      </a>
      {{#if cart.show_multiple_address_shipping}}
        <div class="checkout-multiple-container">
          <a class="checkout-multiple" href="{{urls.checkout.multiple_address}}">
            {{lang 'cart.checkout_multiple'}}
          </a>
        </div>
      {{/if}}
    </div>
  </footer>

  {{> components/cart/cart-preview/cart-preview-navigation}}
{{else}}
  <div class="cart-preview-item-list" data-cart-preview-item-list>
    <div class="cart-preview-empty">
      <p class="cart-preview-empty-message">{{lang 'cart.cart_preview.empty'}}
    </div>
  </div>

  {{> components/cart/cart-preview/cart-preview-navigation}}
{{/if}}

core/products/price:
{{!
  Show product prices

  Prices are fairly complicated. Besides having to be able to show the price
  with and without tax, a merchant can also assign a Recommended Retail Price,
  and a Sale Price.

  This means that in extreme cases, we have to show 4 different prices at once.

  When a Sale Price is set we do not have direct access to it--instead it
  overwrites the regular price.

  @param price object

  @param show_tax_labels bool
    If true, show a label saying the price is with or without tax.

  @param show_savings bool
    If true, show a label saying how much is saved from the full price.

  @param show_sale_badges bool
    If true, show sale badges inline with price.

  @param sale_badge_classes string
    Class to append to sale badge

  @param price_range_display string
    If set to "both", show price as a range when applicable.

  @param show_amp_classes bool
    If true include amp class component
}}
{{#if price}}
   {{#all (if price_range_display '===' 'both') price.price_range}}
    {{> core/products/price-range
      price_range=price.price_range
      show_amp_classes=show_amp_classes
    }}
  {{else}}
    <div
      class="
        price
        {{#all price.without_tax price.with_tax}}multiple-prices{{/all}}
        {{#if show_amp_classes}}
          {{> core/amp/common/price-class}}
        {{/if}}
        "
      >
      {{#if price.without_tax}}
        <div
          class="price-withouttax"
          data-product-price-wrapper="without-tax"
        >

          {{#if price.rrp_without_tax}}
            <div class="price-rrp-wrapper">
              <label class="price-rrp-label">
                {{retail_price_label}}
              </label>
              <span class="price-rrp" data-price-rrp>
                {{price.rrp_without_tax.formatted}}
              </span>
            </div>
          {{/if}}

          {{#if price.non_sale_price_without_tax}}
            <div class="price-ns-wrapper">
              <label class="price-ns-label">
                {{non_sale_price_label}}
              </label>
              <span class="price-ns" data-price-rrp>
                {{price.non_sale_price_without_tax.formatted}}
              </span>
            </div>
          {{/if}}

          <div class="price-value-wrapper">
            {{!-- If there is another price show "now" label --}}
            {{#if price.non_sale_price_without_tax}}
              <label class="price-value-label">
                {{sale_price_label}}
              </label>
            {{else}}
              <label class="price-label">
                {{price_label}}
              </label>
            {{/if}}
            <span class="price-value" data-product-price>
              {{price.without_tax.formatted}}
            </span>

            {{#if show_tax_labels}}
              {{#all price.with_tax price.without_tax}}
                <span class="price-tax-label">
                  {{lang
                    'core.price.without_tax'
                    tax_label=price.tax_label
                  }}
                </span>
              {{/all}}
            {{/if}}

            {{#all show_sale_badges (unless price.with_tax)}}
              {{> core/products/sale-badge
                classes=sale_badge_classes
              }}
            {{/all}}
          </div>

        </div>
      {{/if}}

      {{#if price.with_tax}}
        <div
          class="price-withtax"
          data-product-price-wrapper="with-tax"
        >

          {{#if price.rrp_with_tax}}
            <div class="price-rrp-wrapper">
              <label class="price-rrp-label">
                {{retail_price_label}}
              </label>
              <span class="price-rrp" data-price-rrp>
                {{price.rrp_with_tax.formatted}}
              </span>
            </div>
          {{/if}}

          {{#if price.non_sale_price_with_tax}}
            <div class="price-ns-wrapper">
              <label class="price-ns-label">
                {{non_sale_price_label}}
              </label>
              <span class="price-ns" data-price-rrp>
                {{price.non_sale_price_with_tax.formatted}}
              </span>
            </div>
          {{/if}}

          <div class="price-value-wrapper">
            {{!-- If there is another price show "now" label --}}
            {{#if price.non_sale_price_with_tax}}
              <label class="price-value-label">
                {{sale_price_label}}
              </label>
            {{else}}
              <label class="price-label">
                {{price_label}}
              </label>
            {{/if}}
            <span class="price-value" data-product-price>
              {{price.with_tax.formatted}}
            </span>

            {{#if show_tax_labels}}
              {{#all price.with_tax price.without_tax}}
                <span class="price-tax-label">
                  {{lang
                    'core.price.with_tax'
                    tax_label=price.tax_label
                  }}
                </span>
              {{/all}}
            {{/if}}

            {{#if show_sale_badges}}
              {{> core/products/sale-badge
                classes=sale_badge_classes
              }}
            {{/if}}
          </div>
        </div>
      {{/if}}

      {{#if show_savings}}
        {{#if price.saved}}
          <span class="price-saved" data-product-price-saved>
            {{lang 'core.price.you_save' amount=price.saved.formatted}}
          </span>
        {{/if}}
      {{/if}}
    </div>
  {{/all}}
{{/if}}

core/products/sale-badge:
{{!
  Shows a sale badge if a sale price has been entered

  Themes can use this to display a sale badge.

  @param classes string
    Class(es) to append to sale badge
}}
{{#or price.sale_price_with_tax.value price.sale_price_without_tax.value}}
  <span class="sale-badge {{classes}}">
    {{lang 'core.product.sale'}}
  </span>
{{/or}}

core/products/price-range:
{{!
  Shows a product's price range.

  @param price_range object

  @param show_amp_classes bool
    If true include amp class component
}}
<div
  class="
    price
    {{#if show_amp_classes}}
      {{> core/amp/common/price-class}}
    {{/if}}
  "
>
  {{#and price_range.min.with_tax price_range.max.with_tax}}
    <div class="price-withtax" data-product-price-wrapper="with-tax">
      <span data-product-price-with-tax class="price price-value">
        {{price_range.min.with_tax.formatted}} - {{price_range.max.with_tax.formatted}}
      </span>
      {{#and price_range.min.without_tax price_range.max.without_tax}}
        <span class="price-tax-label">
          {{lang
            'core.price.with_tax'
            tax_label=price.tax_label
          }}
        </span>
      {{/and}}
    </div>
  {{/and}}
  {{#and price_range.min.without_tax price_range.max.without_tax}}
    <div class="price-withouttax" data-product-price-wrapper="without-tax">
      <span data-product-price-without-tax class="price price-without-tax">
        {{price_range.min.without_tax.formatted}} - {{price_range.max.without_tax.formatted}}
      </span>
      {{#and price_range.min.with_tax price_range.max.with_tax}}
        <span class="price-tax-label">
          {{lang
            'core.price.without_tax'
            tax_label=price.tax_label
          }}
        </span>
      {{/and}}
    </div>
  {{/and}}

  <span class="price-saved" data-product-price-saved></span>
</div>

core/amp/common/price-class:
{{#if price.with_tax}}
  {{#unless price.without_tax}}
    {{#if price.rrp_with_tax}}
      prices-taxed-onsale
    {{else}}
      prices-taxed
    {{/if}}
  {{/unless}}
{{/if}}
{{#if price.without_tax}}
  {{#unless price.with_tax}}
    {{#if price.rrp_without_tax}}
      prices-notax-onsale
    {{else}}
      prices-notax
    {{/if}}
  {{/unless}}
{{/if}}
{{#if price.with_tax}}
  {{#if price.without_tax}}
    {{#if price.rrp_with_tax}}
      prices-both-onsale
    {{else}}
      prices-both
    {{/if}}
  {{/if}}
{{/if}}
