components/product/additional-info:
{{#if product.sku}}
  <div class="product-additional-info-container product-additional-info-sku">
    <span class="product-additional-info-key">
      {{lang 'product.sku'}}
    </span>
    <span class="product-additional-info-value" data-product-sku>
      {{product.sku}}
    </span>
  </div>
{{/if}}
{{#if product.upc}}
  <div class="product-additional-info-container product-additional-info-upc">
    <span class="product-additional-info-key">
      {{lang 'product.upc'}}
    </span>
    <span class="product-additional-info-value" data-product-upc>
      {{product.upc}}
    </span>
  </div>
{{/if}}
{{#if product.stock_level}}
  <div class="product-additional-info-container product-additional-info-stock-level">
    <span class="product-additional-info-key">
      {{lang 'product.stock_level'}}
    </span>
    <span class="product-additional-info-value">
      {{product.stock_level}}
    </span>
  </div>
{{/if}}
{{#if product.condition}}
  <div class="product-additional-info-container product-additional-info-condition">
    <span class="product-additional-info-key">
      {{lang 'product.condition'}}
    </span>
    <span class="product-additional-info-value">
      {{product.condition}}
    </span>
  </div>
{{/if}}
{{#if product.availability}}
  <div class="product-additional-info-container product-additional-info-availability">
    <span class="product-additional-info-key">
      {{lang 'product.availability'}}
    </span>
    <span class="product-additional-info-value">
      {{product.availability}}
    </span>
  </div>
{{/if}}
{{#if product.release_date}}
<div class="product-additional-info-container product-additional-info-release-date">
  <span class="product-additional-info-key">
    {{lang 'product.release_date'}}
  </span>
  <span class="product-additional-info-value">
    {{product.release_date}}
  </span>
</div>
{{/if}}
{{#if product.weight}}
  <div class="product-additional-info-container product-additional-info-weight">
    <span class="product-additional-info-key">
      {{lang 'product.weight'}}
    </span>
    <span class="product-additional-info-value" data-product-weight>
      {{product.weight}}
    </span>
  </div>
{{/if}}
{{#if product.width}}
  <div class="product-additional-info-container product-additional-info-weight">
    <span class="product-additional-info-key">
      {{lang 'product.width'}}
    </span>
    <span class="product-additional-info-value" data-product-width>
      {{product.width}}
      {{#if settings.measurements.length '==' 'Centimeters'}}
          ({{lang 'product.measurement.metric'}})
        {{else}}
          ({{lang 'product.measurement.imperial'}})
        {{/if}}
    </span>
  </div>
{{/if}}
{{#if product.height}}
  <div class="product-tab-details-item">
    <span class="product-additional-info-key">
      {{lang 'product.height'}}
    </span>
    <span class="product-additional-info-value" data-product-height>
      {{product.height}}
      {{#if settings.measurements.length '==' 'Centimeters'}}
          ({{lang 'product.measurement.metric'}})
        {{else}}
          ({{lang 'product.measurement.imperial'}})
        {{/if}}
    </span>
  </div>
{{/if}}
{{#if product.depth}}
  <div class="product-tab-details-item">
    <span class="product-additional-info-key">
      {{lang 'product.depth'}}
    </span>
    <span class="product-additional-info-value" data-product-depth>
      {{product.depth}}
      {{#if settings.measurements.length '==' 'Centimeters'}}
          ({{lang 'product.measurement.metric'}})
        {{else}}
          ({{lang 'product.measurement.imperial'}})
        {{/if}}
    </span>
  </div>
{{/if}}
{{#if product.gift_wrapping_available}}
  <div class="product-additional-info-container product-additional-info-gift-wrapping">
    <span class="product-additional-info-key">
      {{lang 'product.gift_wrapping'}}
    </span>
    <span class="product-additional-info-value">
      {{lang 'product.gift_wrapping_available'}}
    </span>
  </div>
{{/if}}
{{#if product.shipping}}
  {{#if product.shipping.calculated}}
    <div class="product-additional-info-container product-additional-info-shipping">
      <span class="product-additional-info-key">
        {{lang 'product.shipping'}}
      </span>
      <span class="product-additional-info-value">
        {{lang 'product.shipping_calculated'}}
      </span>
    </div>
  {{else}}
    {{#if product.shipping.price.value '===' 0}}
      <div class="product-additional-info-container product-additional-info-shipping">
        <span class="product-additional-info-key">
          {{lang 'product.shipping'}}
        </span>
        <span class="product-additional-info-value">
          {{lang 'product.shipping_free'}}
        </span>
      </div>
    {{else}}
      {{#if product.shipping.fixed}}
        <div class="product-additional-info-container product-additional-info-shipping">
          <span class="product-additional-info-key">
            {{lang 'product.shipping'}}
          </span>
          <span class="product-additional-info-value">
            {{lang 'product.shipping_fixed' amount=product.shipping.price.formatted}}
          </span>
        </div>
      {{/if}}
    {{/if}}
  {{/if}}
{{/if}}
{{#if product.custom_fields}}
  {{#each product.custom_fields}}
    <div class="product-additional-info-container product-additional-custom-fields">
      <span class="product-additional-info-key">
        {{name}}
      </span>
      <span class="product-additional-info-value">
        {{{value}}}
      </span>
    </div>
  {{/each}}
{{/if}}
