pages/amp/product:
---
product:
  reviews:
      limit: 10
  related_products:
      limit: {{theme_settings.single-product-related-products-count}}
  videos:
      limit: 10
---

{{#partial 'amp-cart-icon'}}
  <div class="amp-mini-cart">
    <a href="{{urls.cart}}">
      <svg
        class="bag-icon"
        title="Open mini cart"
        role="img"
        width="100%"
        height="100%"
        viewBox="0 0 28 32"
        xmlns="http://www.w3.org/2000/svg"
        xmlns:xlink="http://www.w3.org/1999/xlink"
      >
        <title>icon-bag</title>
        <path d="M2.462 6.585h-0.759l-1.315 24.985h27.533l-1.315-24.985h-0.759z"></path>
        <path d="M9.495 6.688c0.1-0.675 0.279-1.348 0.555-1.968 0.758-1.706 2.069-2.689 4.19-2.689 2.115 0 3.389 0.976 4.097 2.673 0.426 1.021 0.566 2.081 0.556 2.83-0 0.007 1.599 0.048 1.599 0.048 0-0.008 0-0.008 0-0.026 0.013-0.949-0.154-2.209-0.679-3.468-0.942-2.259-2.768-3.657-5.573-3.657-2.8 0-4.652 1.39-5.652 3.64-0.34 0.765-0.556 1.575-0.675 2.383-0.072 0.487-0.096 0.871-0.096 1.106h1.6c0-0.161 0.020-0.469 0.079-0.872z"></path>
      </svg>

      <span class="cart-preview-count" data-cart-preview-count>
        {{#if cart.quantity '>' 0}}
          {{> components/cart/cart-preview/cart-preview-count}}
        {{else}}
          0
        {{/if}}
      </span>
    </a>
  </div>
{{/partial}}

{{#partial 'amp-custom-header-styles'}}
  {{> components/amp/styles/common}}
{{/partial}}

{{> core/amp/pages/product
  header_background=theme_settings.header-background
  header_text=theme_settings.header-color
  body_background=theme_settings.body-background
  color_headings=theme_settings.heading-color
  color_text=theme_settings.body-color
  footer_background=theme_settings.footer-background
  footer_text=theme_settings.footer-color
  color_accent=theme_settings.accent-color
  color_borders=theme_settings.border-color
}}

{{#partial 'amp-custom-product-styles'}}
  {{> components/amp/styles/product}}
{{/partial}}

{{#partial 'amp-additional-info'}}
  {{> components/product/additional-info}}
{{/partial}}

{{#partial 'amp-footer'}}
  {{> core/amp/common/footer
    theme_credits=theme_settings.footer_theme_credits
    theme_name="Scales"
    store_copyright=theme_settings.footer_copyright
  }}
{{/partial}}

{{#partial 'amp-social'}}
  {{> components/amp/social-media-links}}
{{/partial}}

{{#partial 'amp-custom-footer-styles'}}
  {{> components/amp/styles/footer}}
{{/partial}}

{{> layout/amp}}

components/amp/styles/common:
.amp-mini-cart svg {
  width: 25px;
  height: 35px;
  fill: currentColor;
}

.amp-mini-cart a {
  position: relative;
  display: inline-block;
  font-size: 14px;
}

.cart-preview-count {
  position: absolute;
  top: 50%;
  left: 50%;
  height: 25px;
  margin-top: 1px;
  letter-spacing: 0.05em;
  text-transform: uppercase;
  color: {{header_background}};
  transform: translate(-50%, -50%);
}


.amp-grid-item-image-container.amp-image-wide {
  padding-bottom: 66.66667%;
}

.amp-grid-item-image-container.amp-image-square {
  padding-bottom: 100%;
}

.amp-grid-item-image-container.amp-image-tall {
  padding-bottom: 125%;
}

.amp-grid-item-image-container.amp-image-taller {
  padding-bottom: 150%;
}

core/amp/pages/product:
{{#partial "amp-style"}}
  {{> core/amp/products/styles
    header_background=../header_background
    header_text=../header_text
    body_background=../body_background
    color_headings=../color_headings
    color_text=../color_text
    footer_background=../footer_background
    footer_text=../footer_text
    color_accent=../color_accent
    color_borders=../color_borders
  }}
{{/partial}}

{{#partial "amp-scripts"}}
  {{#any product.images product.videos}}
    <script async custom-element="amp-carousel" src="https://cdn.ampproject.org/v0/amp-carousel-0.1.js"></script>
  {{/any}}
  {{#if product.options}}
    <script async custom-element="amp-iframe" src="https://cdn.ampproject.org/v0/amp-iframe-0.1.js"></script>
  {{/if}}
  {{#if product.videos}}
    <script async custom-element="amp-youtube" src="https://cdn.ampproject.org/v0/amp-youtube-0.1.js"></script>
  {{/if}}
  {{#if settings.add_this.buttons}}
    <script async custom-element="amp-social-share" src="https://cdn.ampproject.org/v0/amp-social-share-0.1.js"></script>
  {{/if}}
{{/partial}}


{{#partial "page"}}
  {{> core/amp/common/header}}
  {{> core/amp/products/product-view}}

  {{#block "amp-footer"}} {{/block}}

  {{#if settings.amp_analytics_id}}
    <amp-analytics type="googleanalytics">
      <script type="application/json">
      {
        "vars": {
          "account": "{{settings.amp_analytics_id}}"
        },
        "extraUrlParams": {
          "cd1": "{{page_type}}",
          "cd2": "{{category.id}}",
          "cd3": "{{category.url}}",
          "cd4": "{{category.name}}"
        },
        "triggers": {
          "trackPageview": {
            "on": "visible",
            "request": "pageview"
          },
          "trackClickOnProductCard" : {
            "on": "click",
            "selector": ".amp-grid-item-link, .amp-grid-item-name a",
            "request": "event",
            "vars": {
              "eventCategory": "Category",
              "eventAction": "Product Card"
            },
            "extraUrlParams": {
              "cd6": "${productLink}",
              "cd7": "${productId}",
              "cd8": "${productName}"
            }
          }
        }
      }
      </script>
    </amp-analytics>
  {{/if}}
  {{> core/structured-data/product-reviews}}
{{/partial}}

components/product/additional-info:
{{#if product.sku}}
  <div class="product-additional-info-container product-additional-info-sku">
    <span class="product-additional-info-key">
      {{lang 'product.sku'}}
    </span>
    <span class="product-additional-info-value" data-product-sku>
      {{product.sku}}
    </span>
  </div>
{{/if}}
{{#if product.upc}}
  <div class="product-additional-info-container product-additional-info-upc">
    <span class="product-additional-info-key">
      {{lang 'product.upc'}}
    </span>
    <span class="product-additional-info-value" data-product-upc>
      {{product.upc}}
    </span>
  </div>
{{/if}}
{{#if product.stock_level}}
  <div class="product-additional-info-container product-additional-info-stock-level">
    <span class="product-additional-info-key">
      {{lang 'product.stock_level'}}
    </span>
    <span class="product-additional-info-value">
      {{product.stock_level}}
    </span>
  </div>
{{/if}}
{{#if product.condition}}
  <div class="product-additional-info-container product-additional-info-condition">
    <span class="product-additional-info-key">
      {{lang 'product.condition'}}
    </span>
    <span class="product-additional-info-value">
      {{product.condition}}
    </span>
  </div>
{{/if}}
{{#if product.availability}}
  <div class="product-additional-info-container product-additional-info-availability">
    <span class="product-additional-info-key">
      {{lang 'product.availability'}}
    </span>
    <span class="product-additional-info-value">
      {{product.availability}}
    </span>
  </div>
{{/if}}
{{#if product.release_date}}
<div class="product-additional-info-container product-additional-info-release-date">
  <span class="product-additional-info-key">
    {{lang 'product.release_date'}}
  </span>
  <span class="product-additional-info-value">
    {{product.release_date}}
  </span>
</div>
{{/if}}
{{#if product.weight}}
  <div class="product-additional-info-container product-additional-info-weight">
    <span class="product-additional-info-key">
      {{lang 'product.weight'}}
    </span>
    <span class="product-additional-info-value" data-product-weight>
      {{product.weight}}
    </span>
  </div>
{{/if}}
{{#if product.width}}
  <div class="product-additional-info-container product-additional-info-weight">
    <span class="product-additional-info-key">
      {{lang 'product.width'}}
    </span>
    <span class="product-additional-info-value" data-product-width>
      {{product.width}}
      {{#if settings.measurements.length '==' 'Centimeters'}}
          ({{lang 'product.measurement.metric'}})
        {{else}}
          ({{lang 'product.measurement.imperial'}})
        {{/if}}
    </span>
  </div>
{{/if}}
{{#if product.height}}
  <div class="product-tab-details-item">
    <span class="product-additional-info-key">
      {{lang 'product.height'}}
    </span>
    <span class="product-additional-info-value" data-product-height>
      {{product.height}}
      {{#if settings.measurements.length '==' 'Centimeters'}}
          ({{lang 'product.measurement.metric'}})
        {{else}}
          ({{lang 'product.measurement.imperial'}})
        {{/if}}
    </span>
  </div>
{{/if}}
{{#if product.depth}}
  <div class="product-tab-details-item">
    <span class="product-additional-info-key">
      {{lang 'product.depth'}}
    </span>
    <span class="product-additional-info-value" data-product-depth>
      {{product.depth}}
      {{#if settings.measurements.length '==' 'Centimeters'}}
          ({{lang 'product.measurement.metric'}})
        {{else}}
          ({{lang 'product.measurement.imperial'}})
        {{/if}}
    </span>
  </div>
{{/if}}
{{#if product.gift_wrapping_available}}
  <div class="product-additional-info-container product-additional-info-gift-wrapping">
    <span class="product-additional-info-key">
      {{lang 'product.gift_wrapping'}}
    </span>
    <span class="product-additional-info-value">
      {{lang 'product.gift_wrapping_available'}}
    </span>
  </div>
{{/if}}
{{#if product.shipping}}
  {{#if product.shipping.calculated}}
    <div class="product-additional-info-container product-additional-info-shipping">
      <span class="product-additional-info-key">
        {{lang 'product.shipping'}}
      </span>
      <span class="product-additional-info-value">
        {{lang 'product.shipping_calculated'}}
      </span>
    </div>
  {{else}}
    {{#if product.shipping.price.value '===' 0}}
      <div class="product-additional-info-container product-additional-info-shipping">
        <span class="product-additional-info-key">
          {{lang 'product.shipping'}}
        </span>
        <span class="product-additional-info-value">
          {{lang 'product.shipping_free'}}
        </span>
      </div>
    {{else}}
      {{#if product.shipping.fixed}}
        <div class="product-additional-info-container product-additional-info-shipping">
          <span class="product-additional-info-key">
            {{lang 'product.shipping'}}
          </span>
          <span class="product-additional-info-value">
            {{lang 'product.shipping_fixed' amount=product.shipping.price.formatted}}
          </span>
        </div>
      {{/if}}
    {{/if}}
  {{/if}}
{{/if}}
{{#if product.custom_fields}}
  {{#each product.custom_fields}}
    <div class="product-additional-info-container product-additional-custom-fields">
      <span class="product-additional-info-key">
        {{name}}
      </span>
      <span class="product-additional-info-value">
        {{{value}}}
      </span>
    </div>
  {{/each}}
{{/if}}

components/amp/styles/footer:
.amp-footer-credits {
  font-size: 13px;
}

.footer-social-list {
  margin-left: 0;
  margin-bottom: 40px;
  list-style: none;
}

.footer-social-list-item {
  display: inline-block;
  vertical-align: middle;
  margin: 0 7.5px;
}

.footer-social-list-item:first-of-type {
  margin-left: 0;
}

.footer-social-list-item:last-of-type {
  margin-right: 0;
}

.footer-social-list-item-icon {
  width: 21px;
  height: 21px;
  vertical-align: middle;
  fill: currentColor;
}

core/structured-data/product-reviews:
{{!-- Product Reviews: include in product.html page template --}}

{{#if settings.show_product_reviews}}
  {{#each product.reviews.list}}
    <script type="application/ld+json">
      {
        "@context": "http://schema.org/",
        "@type": "Review",
        "itemReviewed": {
          "@type": "Product",
          "image": "{{getImage ../product.main_image}}",
          "name": "{{../product.title}}",
          "description": "{{../product.description}}",
          "sku": "{{../product.sku}}",
          "brand": {
            "@type": "Thing",
            "name": "{{../product.brand.name}}"
          },
          "aggregateRating": {
            "@type": "AggregateRating",
            "ratingValue": "{{rating}}",
            "reviewCount": "{{../product.num_reviews}}"
          },
          "review": {
            "author": {
              "@type": "Organization",
              "name": "{{../settings.store_name}}"
            },
            "name": "{{product.title}}",
            "reviewRating": {
              "@type": "Rating",
              "ratingValue": "{{rating}}"
            }
          }
        },
        "reviewRating": {
          "@type": "Rating",
          "ratingValue": "{{rating}}"
        },
        "name": "{{title}}",
        "author": {
          "@type": "Person",
          "name": "{{name}}"
        },
        "reviewBody": "{{text}}",
        "publisher": {
          "@type": "Organization",
          "name": "{{../settings.store_name}}"
        }
      }
    </script>
  {{/each}}
{{/if}}

core/amp/css/base:
*,
*:after,
*:before {
  box-sizing: border-box;
}

html {
  font-size: 16px;
}

html,
body {
  height: auto;
}

body {
  font-size: 1rem;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
  font-weight: 400;
  line-height: 1.6;
  color: {{color_text}};
  background: {{body_background}};
  padding-top: 55px;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

ul {
  margin: 0;
  padding: 0;
}

li {
  padding: 0;
  margin: 0;
}

a {
  color: {{color_accent}};
  line-height: inherit;
  text-decoration: none;
}

a:hover,
a:focus {
  opacity: 0.7;
}

