core/amp/css/header:
.amp-header {
  position: fixed;
  top: 0;
  z-index: 20;
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  height: 55px;
  color: {{header_text}};
  background-color: {{header_background}};
  border-bottom: 1px solid {{color_borders}};
}

.amp-header a {
  color: {{header_text}};
}

.amp-mini-cart,
.amp-header-button {
  z-index: 10;
}

.amp-mini-cart {
  margin-right: 15px;
}

.amp-header-button {
  position: relative;
  display: flex;
  align-self: center;
  justify-content: center;
  flex-shrink: 0;
  width: 18px;
  height: 12px;
  margin-left: 15px;
  cursor: pointer;
  background-color: transparent;
  border: 0;
}

.amp-header-close {
  position: absolute;
  right: 0;
  padding: 10px;
  margin-left: 0;
}

.close,
.burger {
  position: absolute;
  display: block;
  width: 100%;
  height: 1px;
  margin-top: -1px;
}

.burger {
  background-color: {{header_text}};
}

.burger-top {
  top: 0;
}

.burger-bottom {
  top: 100%;
}

.burger-middle {
  top: 50%;
}

.close {
  background-color: {{color_text}};
}

.close-left {
  top: 50%;
  transform: rotate(-45deg);
}

.close-right {
  top: 50%;
  transform: rotate(45deg);
}

.arrow-down {
  position: absolute;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  right: 0px;
	transform: rotate(90deg);
  padding: 4px 8px 8px;
}

.amp-header-link {
